import { useEffect, useRef } from "react"

var Btn = props => {
	const elem = useRef(null);

	useEffect(() => {
		elem.current = document.getElementById(props.id);
		elem.current.onclick = e => props.fun();
	} , [])

	useEffect(() =>
		elem.current.style.display = props.display ? "flex" : "none"
	, [props.display])

	return   <button id={props.id} >{props.text}</button>
}



export    default Btn
