import { useState, useEffect } from "react";
import  Btn from "./btn"

var Help = (props) => {
  return (
    <div id="help">
      <div>
          <p>
            player_one controls: <br /> {"← ↑ ↓ →  entre"}
          </p>
          <p>
            player_two controls: <br /> A W S D  space
          </p>
      </div>
    </div>
  );
};

var Controls = (props) => {
	const 	tplayers = props.players;
  return (
    <div id="controls">
      <div id="stats">
        <div>
          <p>
            game state :{" "}
            {props.state < 0
              ? "have't started"
              : props.state == 0
              ? "started"
              : props.state == 1
              ? "pause"
              : props.state == 2
              ? "end"
              : "started"}
          </p>
          <p>total players : {tplayers}</p>
          <p>current players : {props.players}</p>
          <p>
            tournament mode : {tplayers > 2 ? "yes" : "no"},<br/> match{" "}
           1 of 1
          </p>
        </div>
      </div>
      <div >
        <div id="control" className="off">
          {(props.state == -1) ? <Btn text="ENTER" id="enter_btn" class={false} fun={ () => { props.setState(0) } } /> : []}
          {(props.state == 1) ? <Btn text="START" id="start_btn" class={false} fun={ () => { props.setState(3) } } /> : []}
          {(props.state == 3) ? <Btn text="PAUSE" id="pause_btn" class={false} fun={ () => { props.setState(1) } } /> : []}
          {(props.state != -1 && props.state != 2) ? <Btn text="EXIT" id="exit_btn" class={false} fun={ () => { props.setState(2) } } /> : []}
        </div>
      </div>
      <Help players={props.players} />
    </div>
  );
};

export default Controls;
