import { useEffect } from "react";

var canvas,
	ctx,
	wrapper,
	game_state,
	game_update = null,
	current_players,
	score = {p_1: 0, p_2: 0},
	game = [];

var Game = (props) => {
	useEffect(() => {
		canvas = document.getElementById("canvas");
		ctx = canvas.getContext("2d");
		wrapper = document.getElementById("wrapper");
		game.c_wid = canvas.width = wrapper.offsetWidth;
		game.c_hei = canvas.height = window.innerHeight / 2;
		game_update = JSON.parse(props.game_update);
		current_players = props.players;

		game.vars = {
			sq_w: game.c_hei / 10,
			sq_h: game.c_hei / 10,
			main_c: "#e5e5e5",
			main_c_rgb : "rgb(141, 153, 174, ",
			main_b: "#000",
			ps_c : "#fca311",
			coins_c : "#0582ca",
			enemies_MpF: 4,
			enemies_c_f: 0,
			enemies_radius: Math.floor(((game.c_hei / 10) / 3) / 1.5),
			coins_radius: (game.c_hei / 10) / 8,
			players_radius: (game.c_hei / 10) / 5,
			update_margin: 3,
			update_interval: 0
		};

		game.squares = new Array();
		game.enemies = new Array();
		game.coins = new Array();
		game.players = new Array();

		var rand_num = (min, max) => {
			return Math.floor(Math.random() * (max - min + 1) + min);
		};

		game.initiate = () => {
			let a, b, c, d, p=true;
			for (let i = 0; i < game.c_wid - 1; i += game.vars.sq_w) {
				for (
					let j = 0;
					j < game.c_hei - 1 && rand_num(0, i / 3);
					j += game.vars.sq_h
				) {
					d = game.players.length < current_players && (p || i > game.c_wid/2);
					a = !d && (game.squares.length < 20 || rand_num(0, 1));
					b = !d && !a && game.enemies.length < props.enemies && !rand_num(0, 5);
					c = !d && !a && !b && game.coins.length < props.coins && !rand_num(0, 5);

					if (a)
						game.squares.push({
							x: i,
							y: j,
							type: "wall",
							health : rand_num(1, 5),
						});
					else if (b) {
						b = game.vars.sq_w / 3;
						game.enemies.push({
							x: i + b,
							y: j + b,
							mode: !(game.enemies.length % 2),
							dir_x: rand_num(0, 1) ? -1 : 1,
							dir_y: rand_num(0, 1) ? -1 : 1,
							type: "enemy",
							health : !(game.enemies.length % 2) ? 4 : 8,
							max_health : !(game.enemies.length % 2) ? 4 : 8,
						});
					} else if (c) {
						game.coins.push({
							x: i + game.vars.sq_w / 2,
							y: j + game.vars.sq_h / 2,
							type: "coin",
						});
					} else if (d) {
						p = false;
						d = game.vars.sq_w / 2;
						game.players.push({
							x: i + d,
							y: j + d,
							mode: game.players.length,
							dir_x: rand_num(-1, 1),
							dir_y: rand_num(-1, 1),
							shooting: 0,
							type: "player",
							state: 1,
							coins: 0,
							health : 5,
							max_health : 3,
						});
						//}
					}
				}
			}
		};

		game.handle_collision = (cause, target) => {
			let color = game.vars.main_c, update = 0 ,
				log;

			if (target.type == "wall") {
				log = "hit wall, "
				target.health -= 1;
				if (!target.health) {
					game.squares.splice(game.squares.indexOf(target), 1);
					log += "destroyed"
				}
				else log += "health: " + target.health
			}
			else if (target.type == "enemy") {
				log = "hit enemy, "
				target.health -= 1;
				if (!target.health) {
					game.enemies.splice(game.enemies.indexOf(target), 1);
					cause.coins += 2;
					update = 1;
					log += "(got them +2)"
					color = game.vars.ps_c;
				}
				else log += ", health: " + target.health;
			}
			else if (target.type == "player") {
				log = "player got hit "
				update = 2;
				target.health -= 1;
				if (!target.health) {
					game.players[game.players.indexOf(target)].state = 0;
					current_players--;
					log += " and destroyed "
					if (!current_players) {
						log += ", ending game"
						game_state = 2;
						//props.setState(game_state);
					}
					props.setPlayers(current_players);
					color = "red";
				}
				else log += target.health + " more"
			} else if (target.type == "coin") {
				log = "got coin "
				game.coins.splice(game.coins.indexOf(target), 1);
				cause.coins += 1;
				color = game.vars.coins_c;
				update = 1;
				log += "+1"
				if (game.coins.length == 0) {
					game_state = 2;
				}
			}

			if (update) {
				if (update == 1) {
					if (cause.mode == 0) { score.p_1 = cause.coins; props.p_1(cause.coins); }
					else { props.p_2(cause.coins); score.p_2 = cause.coins; }
				}
				else {
					if (target.mode == 0) { props.p_1(target.coins); score.p_1 = target.coins; }
					else { props.p_2(target.coins); score.p_2 = target.coins; }
				}
			}

			let logs = props.log;
			logs.push(
				<p style={{ color: color }}>
				{log}
				</p>
			);

			props.setLogs(logs);
		};

		game.collided = (x, y, w, h, s, ray) => {
			if (x < 0 || x > game.c_wid - w || y < 0 || y > game.c_hei - h) return 4;

			try {
				game.squares.forEach((e) => {
					if (
						e.x <= x + w &&
						e.x + game.vars.sq_w >= x &&
						e.y <= y + h &&
						e.y + game.vars.sq_h >= y
					) {
						if (s.type == "player" && ray) game.handle_collision(s, e);
						throw 1;
					}
				});
				if (ray || s.type == "enemy") {
					game.enemies.forEach((e) => {
						let new_w = game.vars.enemies_radius * 2,
							new_x = e.x - game.vars.enemies_radius,
							new_y = e.y - game.vars.enemies_radius;
						if (
							e != s &&
							new_x < x + w &&
							new_x + new_w > x &&
							new_y < y + h &&
							new_y + new_w > y
						) {
							if (s.type == "player") game.handle_collision(s, e);
							throw 2;
						}
					});
				}
				if (s.type == "player") {
					game.coins.forEach((e) => {
						let new_w = game.vars.coins_radius * 2,
							new_x = e.x - game.vars.coins_radius,
							new_y = e.y - game.vars.coins_radius;
						if (
							e != s &&
							new_x < x + w &&
							new_x + new_w > x &&
							new_y < y + h &&
							new_y + new_w > y
						) {
							game.handle_collision(s, e);
							throw 3;
						}
					});
				}
				game.players.forEach((e) => {
					if (e.state) {
						let new_w = game.vars.players_radius * 2,
							new_x = e.x - game.vars.players_radius,
							new_y = e.y - game.vars.players_radius;
						if (
							e != s &&
							new_x < x + w &&
							new_x + new_w > x &&
							new_y < y + h &&
							new_y + new_w > y
						) {
							if (s.type == "enemy" || (s.type == "player" && ray))
								game.handle_collision(s, e);
							throw 3;
						}
					}
				});
			} catch (c) {
				return c;
			}
			return 0;
		};

		game.continue = () => {
			//  coins
			ctx.fillStyle = game.vars.coins_c;
			game.coins.forEach((e) => {
				ctx.beginPath();
				ctx.arc(e.x, e.y, game.vars.coins_radius, 0, 2 * Math.PI);
				ctx.fill();
			});
			//  squares
			game.squares.forEach((e) => {
				ctx.fillStyle = game.vars.main_c_rgb + ((1-(1/e.health))+0.2) + ")";
				ctx.fillRect(e.x, e.y, game.vars.sq_w, game.vars.sq_h);
			});
			//  enemies
			ctx.strokeStyle = game.vars.main_c;
			game.enemies.forEach((e) => {
				ctx.fillStyle = game.vars.main_c
				ctx.beginPath();
				ctx.arc(e.x, e.y, game.vars.enemies_radius, e.max_health - e.health, 2 * Math.PI);
				if (e.mode) ctx.fill();
				else {
					ctx.fillStyle = game.vars.main_b;
					ctx.fill();
					ctx.stroke();
				}
				//  enemy direction
				let ray_x =
					e.x +
					(e.dir_x < 0 ? -(game.vars.enemies_radius / 3) : e.dir_x > 0 ? game.vars.enemies_radius / 3 : 0),
					ray_y =
					e.y +
					(e.dir_y < 0 ? -(game.vars.enemies_radius / 3) : e.dir_y > 0 ? game.vars.enemies_radius / 3 : 0),
					catcher = 0;
				ctx.beginPath();
				ctx.arc(ray_x, ray_y, game.vars.enemies_radius / 3, 0, 2 * Math.PI);
				ctx.fillStyle = e.mode ? game.vars.main_b : game.vars.main_c;
				ctx.fill();
				// enemy ray
				ctx.beginPath();
				ctx.moveTo(ray_x, ray_y);
				while (
					catcher < game.c_wid &&
					game.collided(ray_x, ray_y, 1, 1, e, true) == 0
				) {
					ray_x += 2 * e.dir_x;
					ray_y += 2 * e.dir_y;
					catcher++;
				}
				ctx.lineTo(ray_x, ray_y);
				ctx.stroke();
			});
			//  players
			ctx.strokeStyle = game.vars.ps_c;
			game.players.forEach((e) => {
				if (e.state) {
					let x_ray =
						e.x +
						(e.dir_x < 0 ? -(game.vars.players_radius / 3) : e.dir_x > 0 ? game.vars.players_radius / 3 : 0),
						y_ray =
						e.y +
						(e.dir_y < 0 ? -(game.vars.players_radius / 3) : e.dir_y > 0 ? game.vars.players_radius / 3 : 0),
						catcher = 0;
					//  player itself
					ctx.fillStyle = game.vars.ps_c;
					ctx.beginPath();
					ctx.arc(e.x, e.y, game.vars.players_radius, e.max_health - e.health, 2 * Math.PI);
					e.mode ? ctx.fill() : ctx.stroke();
					//  player direction
					ctx.beginPath();
					ctx.arc(x_ray, y_ray, game.vars.players_radius / 3, 0, 2 * Math.PI);
					ctx.fillStyle = (e.mode ? game.vars.main_b : game.vars.ps_c);

					ctx.fill();
					//  player shoot
					if (e.shooting > 0) {
						ctx.lineWidth = 2;
						ctx.beginPath();
						ctx.moveTo(x_ray, y_ray);
						while (
							catcher < game.c_wid &&
							game.collided(x_ray, y_ray, 1, 1, e, e.shooting == 1) == 0
						) {
							x_ray += (2 * e.dir_x);
							y_ray += (2 * e.dir_y);
							catcher++;
						}
						ctx.lineTo(x_ray, y_ray);
						ctx.stroke();
						e.shooting += 1;
					}
					if (e.shooting > 2)
						e.shooting = 0;
				}
			});
		};

		game.moveEnemies = () => {
			if (game.vars.enemies_c_f < game.vars.enemies_MpF)
				game.vars.enemies_c_f++;
			else {
				let new_x, new_y;
				game.vars.enemies_c_f = 0;
				game.enemies.forEach((e) => {
					new_x = e.x + 5 * e.dir_x;
					new_y = e.y + 5 * e.dir_y;
					if (
						game.collided(
							new_x - game.vars.enemies_radius,
							new_y - game.vars.enemies_radius,
							game.vars.enemies_radius * 2,
							game.vars.enemies_radius * 2,
							e, false
						) != 0
					) {
						let new_dir_x, new_dir_y;
						new_dir_x = rand_num(-1, 1);
						while ((new_dir_y = rand_num(-1, 1)) == e.dir_y);
						e.dir_x = new_dir_x;
						e.dir_y = !new_dir_y && !new_dir_x ? (rand_num(0, 1) ? -1 : 1) : new_dir_y;
					} else {
						e.x = new_x;
						e.y = new_y;
					}
				});
			}
		};

		game.movePlayer = (p, code) => {
			if (game_state !== 3) return ;
			let p_index = 20,new_x, new_y, p_radius;
			if (game.players.length > 1 && game.players[p].state && ((code <= 40 && code >= 37) || code == 32)) {
				if (code == 32) {
					game.players[p].shooting = 1;
					return ;
				}
				game.players[p].dir_x = code == 37 ? -1 : code == 39 ? 1 : 0;
				game.players[p].dir_y = code == 38 ? -1 : code == 40 ? 1 : 0;
			}
			else return;
			p_radius = game.vars.players_radius;
			new_x = game.players[p].x + p_index * game.players[p].dir_x;
			new_y = game.players[p].y + p_index * game.players[p].dir_y;
			while (
				p_index >= 0 &&
				game.collided(
					new_x - p_radius,
					new_y - p_radius,
					p_radius * 2,
					p_radius * 2,
					game.players[p],
					false
				) != 0
			) {
				new_x = game.players[p].x + p_index * game.players[p].dir_x;
				new_y = game.players[p].y + p_index * game.players[p].dir_y;
				p_index -= 2;
			}
			game.players[p].x = new_x;
			game.players[p].y = new_y;
		}

		game.send_updates = () => {
			props.send_update("game update", JSON.stringify({
				w: game.c_wid,
				h: game.c_hei,
				current_players: current_players,
				players: game.players,
				enemies: game.enemies,
				coins: game.coins,
				squares: game.squares,
				log: props.log,
				p1_score: score.p_1,
				p2_score: score.p_2,
				state: game_state,
			}), 'guest')
		}

		game.receive_updates = () => {
			if (game_update != null) {
				game.movePlayer(1, game_update.keyCode);
				game_update = null;
			}
		}

		game.pause = () => {
			ctx.fillStyle = game.vars.main_c;
			let   pause = "PAUSE";
			ctx.fillText(pause, game.c_wid/2 - pause.length , game.c_hei / 2);
		};

		game.end = () => {
			ctx.fillStyle = game.vars.main_c;
			let   over = "GAME OVER",
			winner;
			if (game.players.length > 0)
				winner = game.players[0].coins > game.players[1].coins ? "PLAYER 1"
				: (game.players[0].coins < game.players[1].coins ? "PLAYER 2" : "NO ONE");
			else	winner = "NO ONE";
			winner += " WON";
			ctx.fillText(over, game.c_wid/2 - (over.length/2)*10, game.c_hei/2);
			ctx.fillText(winner, game.c_wid/2 - (winner.length/2)*10, (game.c_hei/2) + 30);

			setTimeout(() => {
				game.squares = new Array();
				game.enemies = new Array();
				game.coins = new Array();
				game.players = new Array();
				game_state = -1;
				props.toggle();
			}, 3000)
		};

		window.onresize = a => {
			game.c_wid = canvas.width = wrapper.offsetWidth;
			game.c_hei = canvas.height = window.innerHeight / 2;
			canvas.style.width = game.c_wid + "px";
			canvas.style.height = game.c_hei + "px";
			game.vars.sq_w = game.c_hei / 10;
			game.vars.sq_h = game.c_hei / 10;
		};

		game.draw = () => {
			ctx.clearRect(0, 0, game.c_wid, game.c_hei);
			ctx.font = "20px monospace";
			ctx.lineWidth = "2";

			switch (game_state) {
				case 0:
					game.initiate();
					window.onkeydown = (e) => game.movePlayer(0, e.keyCode);
					break;
				case 1:
					game.pause();
					break;
				case 2:
					game.end();
					break;
				case -1:
					break;
				default:
					game.moveEnemies();
					game.continue();
			}
			game.receive_updates();
			if (!game_state)	game_state = 3;
			if (game_state != props.state) 
				props.setState(game_state);
			if (game.vars.update_interval >= game.vars.update_margin)
				{ game.send_updates(); game.vars.update_interval = 0; console.log("host: sending update"); }
			else	game.vars.update_interval += 1;
			requestAnimationFrame(game.draw);
		};
		requestAnimationFrame(game.draw);
	}, []);
	current_players = props.players;
	game_state = props.state;
	if (props.game_update)
		game_update = JSON.parse(props.game_update);
	return <canvas id="canvas" className="off" >something is wrong</canvas>;
};

export default Game;
