import { useEffect, useState, useRef } from 'react';
//import Game from './game.js';
import Btn from './btn.js';
import './styles.css';

/*
state === 0 ?
	"looking for an opponent (matchmaking)"

	: state === 1 ?
		"found an opponent (matchmaking)"

		: state === 2 ?
			"game room"

			: state === 3 ?
			"unfunctional state"

 */

var	App = () => {
	const [socket, setSocket] = useState(null);
	const [state, setState] = useState(0);
	const [room_name, setRoom_name] = useState(0);
	const [message, setMessage] = useState(null);
	const [player1Score, setPlayer1Score] = useState(0);
	const [player2Score, setPlayer2Score] = useState(0);
	const [players, setPlayers] = useState(1);
	const [role, setRole] = useState(null);
	const [obj, setObj] = useState(null);
	const roleRef = useRef(role);
	const stateRef = useRef(state);

	var	connect = () => {
		const	ws = new WebSocket('ws://localhost:8000/ws/game/');
		ws.onmessage = e => {
			var	data = JSON.parse(e.data);
			if (data.msg_type === 'server update') {
				if (data.role)	setRole(data.role);
				if (data.message)	setMessage(data.message);
				if (data.room_name) setRoom_name(data.room_name);
			}
			else if (data.msg_type === 'pair update') {
				if (data.obj && data.target === roleRef.current) 
					setObj(data.obj);
			}
		}
		ws.onerror = e => {
			if (ws.OPEN)	ws.close();
			setMessage("WebSocket error");
		}
		ws.onclose = e => setMessage("WebSocket closed");
		setSocket(ws);
		return	ws;
	}

	var	send_update = (msg, obj, target) => {
		if (!socket.OPEN)	return ;
		socket.send(JSON.stringify({
			obj: obj,
			message: msg,
			target: target,
		}))
	}

	useEffect(() => { roleRef.current = role }, [role])
	useEffect(() => { stateRef.current = state }, [state])

	useEffect(() => {
		const	ws = connect();
		return () => {
			if (ws.OPEN)
				ws.close();
		}
	}, [])

	useEffect(() => {
		if (message === 'pair disconnected') {
			setState(0);
			setPlayers(1);
			if (socket.OPEN)	socket.close();
			connect();
		}
		else if (message === 'you just got a pair') {
			setState(1);
			setPlayers(2);
		}
	}, [message])

	useEffect(() => {
		if (!obj)	return;
		var	data = JSON.parse(obj);
		if (data.state && stateRef.current != data.state)
			setState(data.state);
		if (role === 'guest') {
			////
		}
		else if (role === 'host') {
			////
		}
	}, [obj])

	var	Game = props => {
		var	elem = useRef(null);
		useEffect(() => {
			elem.current = document.getElementById("game");
			for (const div of elem.current.children) {
				div.onclick = e => {
					if (e.classList.contains("_")) {
						e.innerHtml = "<h1>X</h1>";
						e.classList.replace("_", "x");
					}
					else {
						e.innerHtml = "";
						e.classList.replace("x", "_");
					}
				}
			}
		}, [])
		return	(
			<div id="game">
				<div className='_'></div>
				<div className='_'></div>
				<div className='_'></div>
				<div className='_'></div>
				<div className='_'></div>
				<div className='_'></div>
				<div className='_'></div>
				<div className='_'></div>
				<div className='_'></div>
			</div>
		)
	}

	return	(
		<div id="App">
			{
				state === 0 ?
					<div id="looking_up" >
						<div>
						<h1>looking for an oppenent...</h1>
						<p>this matchmaking system is random<br/>and you are now just waiting<br/>
							for anyone to access this same page
						</p>
						</div>
					</div>
				: state === 1 ?
					<div id="found">
					<div>
						<h1>you just found an oppenent({role === 'guest' ? 'host' : 'guest'})</h1>
						{
							role === 'host' ? <Btn text="start" fun={() => {setState(2)}} id="start_btn" display={true} />
							: role === 'guest' ? <p>waiting for him to start...</p>
							: []
						}
						<Btn text="rematch" fun={() => {/*rematch*/}} id="rematch_btn" display={true} />
					</div>
					</div>
				: state === 2 ? <Game />
				: <h1>something went wrong</h1>
			}
			<div id="control">
				<Btn text="quit" fun={() => {setState(0)}} id="quit_btn" display={state === 2} />
				<Btn text="clear/restart" fun={() => {}} id="restart_btn" display={state === 2} />
			</div>
			<div id="stats">
				<p><span>X</span> {player1Score}</p>
				<p><span>O</span> {player2Score}</p>
			</div>
			<div id="console">
				<p>role: {role}</p>
				<p>room: {room_name}</p>
				<p>state: {state === 0 ? "waiting for second connection"
						: state === 1 ? "waiting for peer approval"
						: state === 2 ? "good" : "not good"}</p>
				<p>players: {players}</p>
				<p>player1-score: {player1Score}</p>
				<p>player2-score: {player2Score}</p>
				<p>object: {obj}</p>
				<p>server-message: {message}</p>
			</div>
			<div id="logs">
				<p>player_two: o on x1,y2</p>
				<p>player_one: X on x1,y0</p>
				<p>player_two: o on x1,y2</p>
				<p>player_one: X on x1,y0</p>
				<p>player_two: o on x1,y2</p>
				<p>player_one: X on x1,y0</p>
				<p>player_two: o on x1,y2</p>
				<p>player_one: win!</p>
			</div>
		</div>
	)
}

export	default App;
