import { useEffect, useState } from "react";
import './App.css';

function App() {
	const [socket, setSocket] = useState(null);
	const [role, setRole] = useState(null);
	const [obj, setObj] = useState(null);
	const [id, setId] = useState(null);
	constÂ [message, setMessage] = useState(null);

	useEffect(() => {
		const ws = new WebSocket('ws://localhost:8000/ws/game/');
		ws.onmessage = e => {
			var data = JSON.parse(e.data);
			if (data.message)
				setMessage(data.message);
			if (data.role)
				setRole(data.role);
			if (data.obj)
				setObj(data.obj);
			if (data.id)
				setId(data.id);
		}
		ws.onerror = e => {
			ws.close();
			setMessage("error on socket");
		}
		ws.onclose = e => setMessage("socket closed");
		setSocket(ws);
		return () => {
			if (ws.OPEN)
				ws.close();
			setMessage("socket is done");
		}
	}, [])

	useEffect(() => {
		if (role == 'host')
			setInterval(() => {
				socket.send(JSON.stringify({ message: "you are my guest" }));
			}, 2000)
	}, [role])

	return (
		<div>
			<h1>role: {role}</h1>
			<h3>id: {id}</h3>
			<h2>message: {message}</h2>
			<p>obj: { obj ? obj : [] }</p>
		</div>
	);
}

export default App;
