import { useEffect, useState, useRef } from 'react';
import Console from './console.js';
import Controls from './controls.js';
import Panel from './panel.js';
import Game from './game.js';
import PGame from './panel.js';
import './styles.css';

var	App = () => {
	const [socket, setSocket] = useState(null);
	const [state, setState] = useState(0);
	constÂ [room_name, setRoom_name] = useState(0);
	const [message, setMessage] = useState(null);
	const [player1Score, setPlayer1Score] = useState(0);
	const [player2Score, setPlayer2Score] = useState(0);
	const [players, setPlayers] = useState(0);
	const [role, setRole] = useState(null);
	const [obj, setObj] = useState(null);
	const roleRef = useRef(role);
	const stateRef = useRef(state);

	var	connect = () => {
		const	ws = new WebSocket('ws://localhost:8000/ws/game/');
		ws.onmessage = e => {
			var	data = JSON.parse(e.data);
			if (data.msg_type === 'server update') {
				if (data.role)	setRole(data.role);
				if (data.message)	setMessage(data.message);
				if (data.room_name) setRoom_name(data.room_name);
			}
			else if (data.msg_type === 'pair update') {
				if (data.obj && data.target === roleRef.current) 
					setObj(data.obj);
			}
		}
		ws.onerror = e => {
			if (ws.OPEN)	ws.close();
			setMessage("WebSocket error");
		}
		ws.onclose = e => setMessage("WebSocket closed");
		setSocket(ws);
		return	ws;
	}

	var	send_update = (msg, obj, target) => {
		if (!socket.OPEN)	return ;
		socket.send(JSON.stringify({
			obj: obj,
			message: msg,
			target: target,
		}))
	}

	useEffect(() => { roleRef.current = role }, [role])
	useEffect(() => { stateRef.current = state }, [state])

	useEffect(() => {
		const	ws = connect();
		return () => {
			if (ws.OPEN)
				ws.close();
		}
	}, [])

	useEffect(() => {
		if (message === 'pair disconnected') {
			setState(2);
			if (socket.OPEN)	socket.close();
			connect();
		}
		else if (message === 'you just got a pair')
			setState(1);
	}, [message])

	useEffect(() => {
		if (role === 'guest') {
			var data = JSON.parse(obj);
			if (stateRef.current != data.state)
				setState(data.state);
		}
	}, [obj])

	return	(
		<div>
			<h1>state: {state}</h1>
			<h1>role: {role}</h1>
			<h1>message: {message}</h1>
			<h1>room: {room_name}</h1>
			<h1>players: {players}</h1>
			<h1>player 1 score: {player1Score}</h1>
			<h1>player 2 score: {player2Score}</h1>
			<h1>obj: {obj}</h1>
			{
				role === 'host' ?
					<button onClick={e => {
						setInterval(() => {
							stateRef.current += 1;
							send_update("game update", JSON.stringify({
								state: stateRef.current,
							}), 'guest');
						}, 2000);
					}} >START</button>
				: role === 'guest' ?
					[]
				: []
			}
		</div>
	)
}

export	default App;
