import { useEffect, useState } from "react"
import  Btn from "./btn"

var     Input = props => {
    useEffect(() => {
        let inp = document.getElementById(props.id);
        inp.onchange = () => props.fun(inp.value);
    }, [])
    return         (props.state ? <input id={props.id} type="text" placeholder={props.value}></input>
                    : <input id={props.id} type="text" placeholder={props.value} disabled></input>)
}

var     Panel = props => {
    const [players, setPlayers] = useState(2);
    const [tplayers, settplayers] = useState(players);
    const [current_match, setcurrent_match] = useState(0);
    const [total_matches, settotal_matches] = useState(current_match + 1);
    const [max_enemies, setMax_enemies] = useState(25);

    return      (
    <div id="panel" className="on" >
        <h2>Configs</h2>
        <div>
            <div>
                <div>
                    <label>TOTAL PLAYERS</label>
                    <Input id="t_ps" value={tplayers} fun={v => settplayers(v)} state={false} />
                </div>
                <div>
                    <label>PLAYERS PER MATCH</label>
                    <Input id="ps" value={players} fun={v => setPlayers(v)} state={false} />
                </div>
                <div>
                    <label>TOTAL MATCHES</label>
                    <Input id="t_ms" value={total_matches} fun={v => settotal_matches(v)} state={false} />
                </div>
                <div>
                    <label>MAX ENEMIES</label>
                    <Input id="t_ens" value={max_enemies} fun={v => setMax_enemies(v)} state={false} />
                </div>
            </div>
            <div>
                // MATCHMAKING
	    <br/>SERVER MESSAGE: {props.message}
	    <br/>ROOM NAME: {props.room_name}
	    <br/>ROLE: {props.role}
            </div>
        </div>
        <Btn text="FINISH" id="finish_configs" class={true} fun={()=>{
                props.setPlayers(players);
                props.setState(0);
                props.toggle();
            }} />
    </div>
    )
}


export default      Panel
