import { useEffect, useState, useRef } from 'react';
import Game from './game.js';
import Btn from './btn.js';
import './styles.css';

/*
state === 0 ?
	"looking for an opponent (matchmaking)"

	: state === 1 ?
		"found an opponent (matchmaking)"

		: state === 2 ?
			"game room"

			: state === 3 ?
			"unfunctional state"

 */

var	App = () => {
	const [socket, setSocket] = useState(null);
	const [state, setState] = useState(0);
	const [room_name, setRoom_name] = useState(0);
	const [message, setMessage] = useState(null);
	const [player1Score, setPlayer1Score] = useState(0);
	const [player2Score, setPlayer2Score] = useState(0);
	const [players, setPlayers] = useState(0);
	const [role, setRole] = useState(null);
	const [obj, setObj] = useState(null);
	const roleRef = useRef(role);
	const stateRef = useRef(state);

	var	connect = () => {
		const	ws = new WebSocket('ws://localhost:8000/ws/game/');
		ws.onmessage = e => {
			var	data = JSON.parse(e.data);
			if (data.msg_type === 'server update') {
				if (data.role)	setRole(data.role);
				if (data.message)	setMessage(data.message);
				if (data.room_name) setRoom_name(data.room_name);
			}
			else if (data.msg_type === 'pair update') {
				if (data.obj && data.target === roleRef.current) 
					setObj(data.obj);
			}
		}
		ws.onerror = e => {
			if (ws.OPEN)	ws.close();
			setMessage("WebSocket error");
		}
		ws.onclose = e => setMessage("WebSocket closed");
		setSocket(ws);
		return	ws;
	}

	var	send_update = (msg, obj, target) => {
		if (!socket.OPEN)	return ;
		socket.send(JSON.stringify({
			obj: obj,
			message: msg,
			target: target,
		}))
	}

	useEffect(() => { roleRef.current = role }, [role])
	useEffect(() => { stateRef.current = state }, [state])

	useEffect(() => {
		const	ws = connect();
		return () => {
			if (ws.OPEN)
				ws.close();
		}
	}, [])

	useEffect(() => {
		if (message === 'pair disconnected') {
			setState(2);
			if (socket.OPEN)	socket.close();
			connect();
		}
		else if (message === 'you just got a pair')
			setState(1);
	}, [message])

	useEffect(() => {
		if (!obj)	return;
		var	data = JSON.parse(obj);
		if (data.state && stateRef.current != data.state)
			setState(data.state);
		if (role === 'guest') {
			////
		}
		else if (role === 'host') {
			////
		}
	}, [obj])

	return	(
		<div id="App">
			{
				state === 0 ?
					<div id="looking_up" >
						<h1>looking for an oppenent...</h1>
						<p>this matchmaking system is random<br/>and you are now just waiting<br/>
							for anyone to access this same page
						</p>
					</div>
				: state === 1 ?
					<div id="found">
						<h1>opponent found!</h1>
					</div>
				: state === 2 ?
					<div id="game">
						<div></div>
						<div></div>
						<div></div>
						<div></div>
						<div></div>
						<div></div>
						<div></div>
						<div></div>
						<div></div>
					</div>
				: <h1>something went wrong</h1>
			}
			<div id="control">
				<Btn text="quit" fun={() => {}} id="quit_btn" display={true} />
				<Btn text="restart" fun={() => {}} id="restart_btn" display={true} />
			</div>
			<div id="stats">
				<p>X {player1Score}</p>
				<p>O {player2Score}</p>
			</div>
			<div id="console">
				<p>state: {state}</p>
				<p>role: {role}</p>
				<p>room: {room_name}</p>
				<p>players: {players}</p>
				<p>player1-score: {player1Score}</p>
				<p>player2-score: {player2Score}</p>
				<p>object: {obj}</p>
				<p>server-message: {message}</p>
			</div>
			<div id="logs">
				<p>player_two: o on x1,y2</p>
				<p>player_one: X on x1,y0</p>
				<p>player_two: o on x1,y2</p>
				<p>player_one: X on x1,y0</p>
				<p>player_two: o on x1,y2</p>
				<p>player_one: X on x1,y0</p>
				<p>player_two: o on x1,y2</p>
				<p>player_one: win!</p>
			</div>
		</div>
	)
}

export	default App;
