import { useEffect } from "react";

var canvas,
	ctx,
	wrapper,
	game_state,
	game_update,
	current_players,
	game = [];

var PGame = (props) => {
	useEffect(() => {
		canvas = document.getElementById("canvas");
		ctx = canvas.getContext("2d");
		wrapper = document.getElementById("wrapper");
		game.c_wid = canvas.width = wrapper.offsetWidth;
		game.c_hei = canvas.height = window.innerHeight / 2;
		game_state = props.state;
		game_update = JSON.parse(props.game_update);

		game.vars = {
			sq_w: game.c_hei / 10,
			sq_h: game.c_hei / 10,
			main_c: "#e5e5e5",
			main_c_rgb : "rgb(141, 153, 174, ",
			main_b: "#000",
			ps_c : "#fca311",
			coins_c : "#0582ca",
		};

		game.squares = new Array();
		game.enemies = new Array();
		game.coins = new Array();
		game.players = new Array();

		game.receive_updates = () => {
			if (game_update != null) {
				game_state = game_update.state;
				props.setState(game_state);
				current_players = game_update.current_players;
				props.setPlayers(current_players);
				props.setLogs(game_update.log);
				game.squares = game_update.squares;
				game.players = game_update.players;
				game.enemies = game_update.enemies;
				game.coins = game_update.coins;
				game_update = null;
			}
		}

		game.collided = (x, y, w, h, s, ray) => {
			if (x < 0 || x > game.c_wid - w || y < 0 || y > game.c_hei - h) return 4;

			try {
				game.squares.forEach((e) => {
					if (
						e.x <= x + w &&
						e.x + e.w >= x &&
						e.y <= y + h &&
						e.y + e.h >= y
					) {
						//if (s.type == "player" && ray) game.handle_collision(s, e);
						throw 1;
					}
				});
				if (ray || s.type == "enemy") {
					game.enemies.forEach((e) => {
						let new_w = e.radius * 2,
							new_x = e.x - e.radius,
							new_y = e.y - e.radius;
						if (
							e != s &&
							new_x < x + w &&
							new_x + new_w > x &&
							new_y < y + h &&
							new_y + new_w > y
						) {
							//if (s.type == "player") game.handle_collision(s, e);
							throw 2;
						}
					});
				}
				if (s.type == "player") {
					game.coins.forEach((e) => {
						let new_w = e.radius * 2,
							new_x = e.x - e.radius,
							new_y = e.y - e.radius;
						if (
							e != s &&
							new_x < x + w &&
							new_x + new_w > x &&
							new_y < y + h &&
							new_y + new_w > y
						) {
							//game.handle_collision(s, e);
							throw 3;
						}
					});
				}
				game.players.forEach((e) => {
					if (e.state) {
						let new_w = e.radius * 2,
							new_x = e.x - e.radius,
							new_y = e.y - e.radius;
						if (
							e != s &&
							new_x < x + w &&
							new_x + new_w > x &&
							new_y < y + h &&
							new_y + new_w > y
						) {
							/*if (s.type == "enemy" || (s.type == "player" && ray))
								game.handle_collision(s, e);*/
							throw 3;
						}
					}
				});
			} catch (c) {
				return c;
			}
			return 0;
		};

		game.continue = () => {
			//  coins
			ctx.fillStyle = game.vars.coins_c;
			game.coins.forEach((e) => {
				ctx.beginPath();
				ctx.arc(e.x, e.y, e.radius, 0, 2 * Math.PI);
				ctx.fill();
			});
			//  squares
			game.squares.forEach((e) => {
				ctx.fillStyle = game.vars.main_c_rgb + ((1-(1/e.health))+0.2) + ")";
				ctx.fillRect(e.x, e.y, e.w, e.h);
			});
			//  enemies
			ctx.strokeStyle = game.vars.main_c;
			game.enemies.forEach((e) => {
				ctx.fillStyle = game.vars.main_c
				ctx.beginPath();
				ctx.arc(e.x, e.y, e.radius, e.max_health - e.health, 2 * Math.PI);
				if (e.mode) ctx.fill();
				else {
					ctx.fillStyle = game.vars.main_b;
					ctx.fill();
					ctx.stroke();
				}
				//  enemy direction
				let ray_x =
					e.x +
					(e.dir_x < 0 ? -(e.radius / 3) : e.dir_x > 0 ? e.radius / 3 : 0),
					ray_y =
					e.y +
					(e.dir_y < 0 ? -(e.radius / 3) : e.dir_y > 0 ? e.radius / 3 : 0),
					catcher = 0;
				ctx.beginPath();
				ctx.arc(ray_x, ray_y, e.radius / 3, 0, 2 * Math.PI);
				ctx.fillStyle = e.mode ? game.vars.main_b : game.vars.main_c;
				ctx.fill();
				// enemy ray
				ctx.beginPath();
				ctx.moveTo(ray_x, ray_y);
				while (
					catcher < game.c_wid &&
					game.collided(ray_x, ray_y, 1, 1, e, true) == 0
				) {
					ray_x += 2 * e.dir_x;
					ray_y += 2 * e.dir_y;
					catcher++;
				}
				ctx.lineTo(ray_x, ray_y);
				ctx.stroke();
			});
			//  players
			ctx.strokeStyle = game.vars.ps_c;
			game.players.forEach((e) => {
				if (e.state) {
					let x_ray =
						e.x +
						(e.dir_x < 0 ? -(e.radius / 3) : e.dir_x > 0 ? e.radius / 3 : 0),
						y_ray =
						e.y +
						(e.dir_y < 0 ? -(e.radius / 3) : e.dir_y > 0 ? e.radius / 3 : 0),
						catcher = 0;
					//  player itself
					ctx.fillStyle = game.vars.ps_c;
					ctx.beginPath();
					ctx.arc(e.x, e.y, e.radius, e.max_health - e.health, 2 * Math.PI);
					e.mode ? ctx.fill() : ctx.stroke();
					//  player direction
					ctx.beginPath();
					ctx.arc(x_ray, y_ray, e.radius / 3, 0, 2 * Math.PI);
					ctx.fillStyle = (e.mode ? game.vars.main_b : game.vars.ps_c);

					ctx.fill();
					//  player shoot
					if (e.shooting > 0) {
						ctx.lineWidth = 2;
						ctx.beginPath();
						ctx.moveTo(x_ray, y_ray);
						while (
							catcher < game.c_wid &&
							game.collided(x_ray, y_ray, 1, 1, e, e.shooting == 1) == 0
						) {
							x_ray += (2 * e.dir_x);
							y_ray += (2 * e.dir_y);
							catcher++;
						}
						ctx.lineTo(x_ray, y_ray);
						ctx.stroke();
						e.shooting += 1;
					}
					if (e.shooting > 2)
						e.shooting = 0;
				}
			});
		};

		game.movePlayer = code => {
			let p_index = 20,new_x, new_y, p_radius, p;
			if (game.players.length > 1 && game.players[1].state
				&& (code == 32 || code == 65 || code == 83 || code == 68 || code == 87)) {
				p = 1;
				if (code == 32) {
					game.players[p].shooting = 1;
					return ;
				}
				game.players[p].dir_x = code == 65 ? -1 : code == 68 ? 1 : 0;
				game.players[p].dir_y = code == 87 ? -1 : code == 83 ? 1 : 0;
			}
			else return;
			p_radius = game.players[p].radius;
			new_x = game.players[p].x + p_index * game.players[p].dir_x;
			new_y = game.players[p].y + p_index * game.players[p].dir_y;
			while (
				p_index >= 0 &&
				game.collided(
					new_x - p_radius,
					new_y - p_radius,
					p_radius * 2,
					p_radius * 2,
					game.players[p],
					false
				) != 0
			) {
				new_x = game.players[p].x + p_index * game.players[p].dir_x;
				new_y = game.players[p].y + p_index * game.players[p].dir_y;
				p_index -= 2;
			}
			game.players[p].x = new_x;
			game.players[p].y = new_y;
		}

		game.pause = () => {
			ctx.fillStyle = game.vars.main_c;
			let   pause = "PAUSE";
			ctx.fillText(pause, game.c_wid/2 - pause.length , game.c_hei / 2);
		};

		game.end = () => {
			ctx.fillStyle = game.vars.main_c;
			let   over = "GAME OVER",
				winner = game.players[0].coins > game.players[1].coins ? "PLAYER 1"
				: (game.players[0].coins < game.players[1].coins ? "PLAYER 2" : "NO ONE");
			winner += " WON";
			ctx.fillText(over, game.c_wid/2 - (over.length/2)*10, game.c_hei/2);
			ctx.fillText(winner, game.c_wid/2 - (winner.length/2)*10, (game.c_hei/2) + 30);

			setTimeout(() => {
				game.squares = new Array();
				game.enemies = new Array();
				game.coins = new Array();
				game.players = new Array();
				game_state = -1;
				props.setState(game_state);
				props.toggle();
			}, 3000)
		};

		window.onresize = a => {
			game.c_wid = canvas.width = wrapper.offsetWidth;
			game.c_hei = canvas.height = window.innerHeight / 2;
			canvas.style.width = game.c_wid + "px";
			canvas.style.height = game.c_hei + "px";
			game.vars.sq_w = game.c_hei / 10;
			game.vars.sq_h = game.c_hei / 10;
		};

		game.draw = () => {
			ctx.clearRect(0, 0, game.c_wid, game.c_hei);
			ctx.font = "20px monospace";
			ctx.lineWidth = "2";

			switch (game_state) {
				case 0:
					window.onkeydown = (e) => game.movePlayer(e.keyCode);
					game_state = 3;
					props.setState(game_state);
					break;
				case 1:
					game.pause();
					break;
				case 2:
					game.end();
					break;
				case -1:
					break;
				default:
					game.receive_updates();
					game.continue();
			}
			requestAnimationFrame(game.draw);
		};
		requestAnimationFrame(game.draw);
	}, []);
	game_state = props.state;
	game_update = JSON.parse(props.game_update);
	current_players = props.players;
	return <canvas id="canvas" className="off" >something is wrong</canvas>;
};

export default PGame;
