import { useEffect, useState } from "react";
import './App.css';

function App() {
	const [socket, setSocket] = useState(null);
	const [role, setRole] = useState(null);
	const [obj, setObj] = useState(null);
	//
	constÂ [message, setMessage] = useState(null);

	useEffect(() => {
		const ws = new WebSocket('ws://localhost:8000/ws/game');
		ws.onmessage = e => {
			var data = JSON.parse(e.data);
			if (data.message)
				setMessage(data.message);
			if (data.role)
				setRole(data.role);
			if (data.obj)
				setObj(data.obj);
		}
		ws.onerror = e => {
			ws.close();
			setMessage("error on socket");
		}
		ws.onclose = e => setMessage("socket closed");
		setSocket(ws);
		return () => {
			if (ws.OPEN)
				ws.close();
			setMessage("socket is done");
		}
	}, [])

	return (
		<div>
			<h1>{role}</h1>
			<p>{obj}</p>
		</div>
	);
}

export default App;
