import { useEffect, useState, useRef } from 'react';
import Btn from './btn.js';
import './styles.css';

var	App = () => {
	const [socket, setSocket] = useState(null);
	const [state, setState] = useState(0);
	const [room_name, setRoom_name] = useState(0);
	const [message, setMessage] = useState(null);
	const [player1Score, setPlayer1Score] = useState(0);
	const [player2Score, setPlayer2Score] = useState(0);
	const [players, setPlayers] = useState(1);
	const [role, setRole] = useState(null);
	const [obj, setObj] = useState(null);
	const [game_state, setGame_state] = useState(new Array);
	const roleRef = useRef(role);
	const stateRef = useRef(state);

	var	connect = () => {
		const	ws = new WebSocket('ws://localhost:8000/ws/game/');
		ws.onmessage = e => {
			var	data = JSON.parse(e.data);
			if (data.msg_type === 'server update') {
				if (data.role)	setRole(data.role);
				if (data.message)	setMessage(data.message);
				if (data.room_name) setRoom_name(data.room_name);
			}
			else if (data.msg_type === 'pair update') {
				if (data.obj && data.target === roleRef.current) 
					setObj(data.obj);
			}
		}
		ws.onerror = e => {
			if (ws.OPEN)	ws.close();
			setMessage("WebSocket error");
		}
		ws.onclose = e => setMessage("WebSocket closed");
		setSocket(ws);
		return	ws;
	}

	var	send_update = (msg, object, target) => {
		if (!socket || !socket.OPEN || !object)	return;
		socket.send(JSON.stringify({
			obj: object,
			message: msg,
			target: target,
		}))
	}

	useEffect(() => {
		const ws = connect();
		return () => {
			if (ws.OPEN)
				ws.close();
		}
	}, [])

	useEffect(() => {
		if (message === 'pair disconnected') {
			setState(0);
			setPlayers(1);
			if (socket.OPEN)	socket.close();
			connect();
		}
		else if (message === 'you just got a pair') {
			setState(1);
			setPlayers(2);
		}
	}, [message])

	useEffect(() => {
		if (!obj)	return;
		var	data = JSON.parse(obj);
		if (data.state && stateRef.current != data.state)
			setState(data.state);
	}, [obj])

	var	Game = props => {
		var	elem = useRef(null);
		var	game = useRef(props.game_state);
		var	sign = (roleRef.current === 'host') ? "x" : "o";

		useEffect(() => {
			if (game.current.length === 0) {
				for (let i = 0; i < 9; i++) {
					game.current.push({
						"Id": `_game_div_${i}_`,
						"Attr": "DIV",
						"Class": "_",
						"C": "",
						"Evnt": function(e) {
							if (this.Class === "_") {
								this.Class = sign;
								this.C = `<h1>${sign}</h1>`;
								e.classList.replace("_", this.Class);
								e.innerHTML = this.C;
							}
						},
					})
				}
				props.setGame_state(game.current);
			}
			if (elem.current === null)	elem.current = document.getElementById("game");
		}, [])

		useEffect(() => {
			game.current = props.game_state;
			for (let i of game.current) {
				let new_elem = document.createElement("div");
				new_elem.classList.add(i.Class);
				new_elem.id = i.Id;
				new_elem.innerHTML = i.C;
				new_elem.onclick = e => i.Evnt(e.target);
				elem.current.appendChild(new_elem);
			}
			return () => {
				for (let i of elem.current.children)
					elem.current.removeChild(i);
				game.current = new Array;
			}
		}, [props.game_state])

		return	<div id="game"></div>
	}

	useEffect(() => { roleRef.current = role }, [role])
	useEffect(() => {
		stateRef.current = state;
		if (stateRef.current === 2)
			send_update("state update", JSON.stringify({
				state: stateRef.current,
			}), roleRef.current === 'host' ? 'guest' : 'host');
	}, [state])

	return	(
		<div id="App">
			{
				state === 0 ?
					<div id="looking_up" >
						<div>
						<h1>looking for an opponent...{/*TODO animation*/}</h1>
						<p>this matchmaking system is random<br/>and you are now just waiting<br/>
							for anyone to access this same page
						</p>
						</div>
					</div>
				: state === 1 ?
					<div id="found">
					<div>
						<h1>you just found an oppenent({role === 'guest' ? 'host' : 'guest'})</h1>
						{
							role === 'host' ? <Btn text="start" fun={() => {setState(2)}} id="start_btn" display={true} />
							: role === 'guest' ? <p>waiting for him to start...{/*TODO animation*/}</p>
							: []
						}
						<Btn text="rematch" fun={() => {/*TODO rematch*/}} id="rematch_btn" display={true} />
					</div>
					</div>
				: state === 2 ? <Game game_state={game_state} setGame_state={setGame_state} />
				: <h1>something went wrong</h1>
			}
			<div id="control">
				<Btn text="quit" fun={() => {setState(0)}} id="quit_btn" display={state === 2} />
				<Btn text="clear/restart" fun={() => {/*TODO rematch*/}} id="restart_btn" display={state === 2} />
			</div>
			<div id="stats">
				<p><span>X</span> {player1Score}</p>
				<p><span>O</span> {player2Score}</p>
			</div>
			<div id="console">
				<p>role: {role}</p>
				<p>room: {room_name}</p>
				<p>state: {state === 0 ? "waiting for second connection"
						: state === 1 ? "waiting for peer approval"
						: state === 2 ? "good" : "not good"}</p>
				<p>players: {players}</p>
				<p>player1-score: {player1Score}</p>
				<p>player2-score: {player2Score}</p>
				<p>object: {obj}</p>
				<p>server-message: {message}</p>
			</div>
			<div id="logs">
				<p>player_two: o on x1,y2</p>
				<p>player_one: X on x1,y0</p>
				<p>player_two: o on x1,y2</p>
				<p>player_one: X on x1,y0</p>
				<p>player_two: o on x1,y2</p>
				<p>player_one: X on x1,y0</p>
				<p>player_two: o on x1,y2</p>
				<p>player_one: win!</p>
			</div>
		</div>
	)
}

export	default App;
