import { useEffect, useState, useRef } from "react";
import './App.css';

function App() {
	const [socket, setSocket] = useState(null);
	const [role, setRole] = useState(null);
	const [obj, setObj] = useState(null);
	const [room_name, setRoom_name] = useState(null);
	const [message, setMessage] = useState(null);
	const roleRef = useRef(null);

	useEffect(() => {
		const ws = new WebSocket('ws://localhost:8000/ws/game/');
		ws.onmessage = e => {
			var data = JSON.parse(e.data);
			console.log("current role: ", role);
			console.log("a new message: ", data);
			if (!role && data.role)
				setRole(data.role);
			if (data.message && (data.room_name || data.target === roleRef.current))
				setMessage(data.message);
			if (data.obj && data.target === roleRef.current)
				setObj(data.obj);
			if (data.room_name)
				setRoom_name(data.room_name);
		}
		ws.onerror = e => {
			ws.close();
			setMessage("error on socket");
		}
		ws.onclose = e => {setMessage("socket closed")}
		setSocket(ws);
		return () => {
			if (ws.OPEN)
				ws.close();
			setMessage("socket is done");
		}
	}, [])

	return (
		<div>
			<h1>role: {role}</h1>
			<h3>room_name: {room_name}</h3>
			<h2>message: {message}</h2>
			<p>obj: { obj ? obj : [] }</p>
			<button onClick={ () => {
				socket.send(JSON.stringify({
					update_type : role === "host" ? 'game update' : 'player position',
					obj : role === "host" ? JSON.stringify({ name: 'aname', type: 'multiplayer'}) : JSON.stringify({ x: 10, y: 30 }),
					target : role === "host" ? 'guest' : 'host',
				}))
			} } > { role === "host" ? "send update" : "send position" } </button>
		</div>
	);
}

export default App;
