import { useEffect } from "react";

var canvas,
	ctx,
	wrapper,
	game_state,
	game_update = null,
	current_players,
	_margin = {},
	game = [];

var PGame = (props) => {
	useEffect(() => {
		canvas = document.getElementById("canvas");
		ctx = canvas.getContext("2d");
		wrapper = document.getElementById("wrapper");
		game.c_wid = canvas.width = wrapper.offsetWidth;
		game.c_hei = canvas.height = window.innerHeight / 2;
		game_update = JSON.parse(props.game_update);

		game.vars = {
			sq_w: game.c_hei / 10,
			sq_h: game.c_hei / 10,
			main_c: "#e5e5e5",
			main_c_rgb : "rgb(141, 153, 174, ",
			main_b: "#000",
			ps_c : "#fca311",
			coins_c : "#0582ca",
			enemies_radius: Math.floor(((game.c_hei / 10) / 3) / 1.5),
			players_radius: (game.c_hei / 10) / 5,
			coins_radius: (game.c_hei / 10) / 8
		};

		game.squares = new Array();
		game.enemies = new Array();
		game.coins = new Array();
		game.players = new Array();

		game.receive_updates = () => {
			if (game_update != null && game_update.state == null) {
				_margin.x = game_update.w / game.c_wid;
				_margin.y = game_update.h / game.c_hei;

				if (game_update.state === 0
					|| (game_update.state === 3 && game_state !== 3))
					props.toggle(true);

				current_players = game_update.current_players;
				props.setPlayers(current_players);

				game.squares = game_update.squares;
				game.players = game_update.players;
				game.enemies = game_update.enemies;
				game.coins = game_update.coins;

				props.p_1(game_update.p1_score);
				props.p_2(game_update.p2_score);

				game_update = null;
			}
		}

		game.send_updates = up_type => {
			props.send_update(up_type, JSON.stringify({
				state: null,
				player: game.players[1]
			}), 'host')
		}

		game.send_state = () => {
			props.send_update("game state", JSON.stringify({
				state: game_state,
			}), 'host')
		}

		game.receive_state = () => {
			if (game_update != null && game_update.state != null)
				{ game_state = game_update.state; game_update = null; console.log("guest: received state: ", game_state); }
		}

		game.collided = (x, y, w, h, s, ray) => {
			if (x < 0 || x > game.c_wid - w || y < 0 || y > game.c_hei - h) return 4;
			let e_x, e_y;
			try {
				game.squares.forEach((e) => {
					e_x = e.x / _margin.x;
					e_y = e.y / _margin.y;
					if (
						e_x <= x + w &&
						e_x + game.vars.sq_w >= x &&
						e_y <= y + h &&
						e_y + game.vars.sq_h >= y
					) {
						throw 1;
					}
				});
				if (ray || s.type == "enemy") {
					game.enemies.forEach((e) => {
						let new_w = game.vars.enemies_radius * 2,
							new_x = (e.x/_margin.x) - game.vars.enemies_radius,
							new_y = (e.y/_margin.y) - game.vars.enemies_radius;
						if (
							e != s &&
							new_x < x + w &&
							new_x + new_w > x &&
							new_y < y + h &&
							new_y + new_w > y
						) {
							throw 2;
						}
					});
				}
				if (s.type == "player") {
					game.coins.forEach((e) => {
						let new_w = game.vars.coins_radius * 2,
							new_x = (e.x/_margin.x) - game.vars.coins_radius,
							new_y = (e.y/_margin.y) - game.vars.coins_radius;
						if (
							e != s &&
							new_x < x + w &&
							new_x + new_w > x &&
							new_y < y + h &&
							new_y + new_w > y
						) {
							throw 3;
						}
					});
				}
				game.players.forEach((e) => {
					if (e.state) {
						let new_w = game.vars.players_radius * 2,
							new_x = e.x/_margin.x - game.vars.players_radius,
							new_y = e.y/_margin.y - game.vars.players_radius;
						if (
							e != s &&
							new_x < x + w &&
							new_x + new_w > x &&
							new_y < y + h &&
							new_y + new_w > y
						) {
							throw 3;
						}
					}
				});
			} catch (c) {
				return c;
			}
			return 0;
		};

		game.continue = () => {
			//  coins
			ctx.fillStyle = game.vars.coins_c;
			game.coins.forEach((e) => {
				ctx.beginPath();
				ctx.arc(e.x / _margin.x, e.y / _margin.y, game.vars.coins_radius, 0, 2 * Math.PI);
				ctx.fill();
			});
			//  squares
			game.squares.forEach((e) => {
				ctx.fillStyle = game.vars.main_c_rgb + ((1-(1/e.health))+0.2) + ")";
				ctx.fillRect(e.x / _margin.x, e.y / _margin.y, game.vars.sq_w, game.vars.sq_h);
			});
			//  enemies
			ctx.strokeStyle = game.vars.main_c;
			game.enemies.forEach((e) => {
				let e_x = e.x / _margin.x,
				e_y = e.y / _margin.y;
				ctx.fillStyle = game.vars.main_c
				ctx.beginPath();
				ctx.arc(e_x, e_y, game.vars.enemies_radius, e.max_health - e.health, 2 * Math.PI);
				if (e.mode) ctx.fill();
				else {
					ctx.fillStyle = game.vars.main_b;
					ctx.fill();
					ctx.stroke();
				}
				//  enemy direction
				let ray_x = e_x + (e.dir_x < 0 ? -(game.vars.enemies_radius / 3) : e.dir_x > 0 ? game.vars.enemies_radius / 3 : 0),
				ray_y = e_y + (e.dir_y < 0 ? -(game.vars.enemies_radius / 3) : e.dir_y > 0 ? game.vars.enemies_radius / 3 : 0),
					catcher = 0;
				ctx.beginPath();
				ctx.arc(ray_x, ray_y, game.vars.enemies_radius / 3, 0, 2 * Math.PI);
				ctx.fillStyle = e.mode ? game.vars.main_b : game.vars.main_c;
				ctx.fill();
				// enemy ray
				ctx.beginPath();
				ctx.moveTo(ray_x, ray_y);
				while (
					catcher < game.c_wid &&
					game.collided(ray_x, ray_y, 1, 1, e, true) == 0
				) {
					ray_x += 2 * e.dir_x;
					ray_y += 2 * e.dir_y;
					catcher++;
				}
				ctx.lineTo(ray_x, ray_y);
				ctx.stroke();
			});
			//  players
			ctx.strokeStyle = game.vars.ps_c;
			game.players.forEach((e) => {
				if (e.state) {
					let e_x = e.x / _margin.x,
						e_y = e.y / _margin.y;
					let x_ray = e_x + (e.dir_x < 0 ? -(game.vars.players_radius / 3) : e.dir_x > 0 ? game.vars.players_radius / 3 : 0),
					y_ray = e_y + (e.dir_y < 0 ? -(game.vars.players_radius / 3) : e.dir_y > 0 ? game.vars.players_radius / 3 : 0),
					catcher = 0;
					//  player itself
					ctx.fillStyle = game.vars.ps_c;
					ctx.beginPath();
					ctx.arc(e_x, e_y, game.vars.players_radius, e.max_health - e.health, 2 * Math.PI);
					e.mode ? ctx.fill() : ctx.stroke();
					//  player direction
					ctx.beginPath();
					ctx.arc(x_ray, y_ray, game.vars.players_radius / 3, 0, 2 * Math.PI);
					ctx.fillStyle = (e.mode ? game.vars.main_b : game.vars.ps_c);

					ctx.fill();
					//  player shoot
					if (e.shooting > 0) {
						ctx.lineWidth = 2;
						ctx.beginPath();
						ctx.moveTo(x_ray, y_ray);
						while (
							catcher < game.c_wid &&
							game.collided(x_ray, y_ray, 1, 1, e, e.shooting == 1) == 0
						) {
							x_ray += (2 * e.dir_x);
							y_ray += (2 * e.dir_y);
							catcher++;
						}
						ctx.lineTo(x_ray, y_ray);
						ctx.stroke();
						e.shooting += 1;
					}
					if (e.shooting > 2)
						e.shooting = 0;
				}
			});
		};

		/*game.movePlayer = code => {
			////
		}*/

		game.movePlayer = code => {
			let p_index = 20,new_x, new_y, p_radius, p;
			if (game.players.length > 1 && game.players[0].state && (code <= 40 && code >= 37 || code == 13)) {
				p = 1;
				if (code == 13) {
					game.players[p].shooting = 1;
					return ;
				}
				game.players[p].dir_x = code == 37 ? -1 : code == 39 ? 1 : 0;
				game.players[p].dir_y = code == 38 ? -1 : code == 40 ? 1 : 0;
			}
			else return;	
			p_radius = game.vars.players_radius;
			new_x = game.players[p].x + p_index * game.players[p].dir_x;
			new_y = game.players[p].y + p_index * game.players[p].dir_y;
			while (
				p_index >= 0 &&
				game.collided(
					new_x - p_radius,
					new_y - p_radius,
					p_radius * 2,
					p_radius * 2,
					game.players[p],
					false
				) != 0
			) {
				new_x = game.players[p].x + p_index * game.players[p].dir_x;
				new_y = game.players[p].y + p_index * game.players[p].dir_y;
				p_index -= 2;
			}
			game.players[p].x = new_x;
			game.players[p].y = new_y;
			game.send_updates("player movements");
		}

		game.pause = () => {
			ctx.fillStyle = game.vars.main_c;
			let   pause = "PAUSE";
			ctx.fillText(pause, game.c_wid/2 - pause.length , game.c_hei / 2);
		};

		game.end = () => {
			ctx.fillStyle = game.vars.main_c;
			let   over = "GAME OVER",
				winner = "NO ONE WON";
			ctx.fillText(over, game.c_wid/2 - (over.length/2)*10, game.c_hei/2);
			ctx.fillText(winner, game.c_wid/2 - (winner.length/2)*10, (game.c_hei/2) + 30);

			setTimeout(() => {
				game.squares = new Array();
				game.enemies = new Array();
				game.coins = new Array();
				game.players = new Array();
				game_state = -1;
				props.toggle(false);
			}, 3000)
		};

		window.onresize = a => {
			game.c_wid = canvas.width = wrapper.offsetWidth;
			game.c_hei = canvas.height = window.innerHeight / 2;
			canvas.style.width = game.c_wid + "px";
			canvas.style.height = game.c_hei + "px";
			game.vars.sq_w = game.c_hei / 10;
			game.vars.sq_h = game.c_hei / 10;
		};

		window.onkeydown = (e) => game.movePlayer(e.keyCode);
		game.draw = () => {
			ctx.clearRect(0, 0, game.c_wid, game.c_hei);
			ctx.font = "20px monospace";
			ctx.lineWidth = "2";

			game.receive_state();
			switch (game_state) {
				case 0:
					break;
				case 1:
					game.pause();
					break;
				case 2:
					game.end();
					break;
				case -1:
					break;
				default:
					game.continue();
			}
			game.receive_updates();
			props.setState(game_state);
			props.setPlayers(current_players);
			requestAnimationFrame(game.draw);
		};
		requestAnimationFrame(game.draw);
	}, []);
	game_state = props.state;
	game_update = JSON.parse(props.game_update);
	return <canvas id="canvas" className="off" >something is wrong</canvas>;
};

export default PGame;
